            .handleLoadingAndError(with: view)
            .asDriver(onErrorDriveWith: .empty())
    }

    func handle(
        register: Signal<Void>,
        email: Driver<String?>,
        password: Driver<String?>,
        shouldRemember: Driver<Bool>
    ) {
        let inputs = Driver.combineLatest(
            email.compactMap { $0 },
