import Foundation
import RxSwift

final class SettingsInteractor {

    private let service: APIService
    private let sessionManager: SessionManager
    private let authStorage: AuthStorageInterface

    init(
        service: APIService = APIService.instance,
        sessionManager: SessionManager = .default,
        authStorage: AuthStorageInterface = AuthStorage.instance
    ) {
        self.service = service
        self.sessionManager = sessionManager
        self.authStorage = authStorage
    }
}

// MARK: - Extensions -

extension SettingsInteractor: SettingsInteractorInterface {

    var user: Single<User> {
        service.rx
            .request(
                UserResponse.self,
                router: SettingsRouter.user,
                session: sessionManager.session
            )
            .map { $0.user }
    }

    func updateUser(with data: Data) -> Single<User> {
        service.rx
            .request(
                UserResponse.self,
                router: SettingsRouter.updateUser(with: data),
                session: sessionManager.session
            )
            .map { $0.user }
    }

    func deleteAuthInfo() {
        authStorage.deleteAuthInfo()
    }
}
