import UIKit
import RxSwift
import RxCocoa

final class TopRatedWireframe: BaseWireframe<TopRatedViewController> {

    // MARK: - Private properties -

    // MARK: - Module setup -

    init() {
        let moduleViewController = TopRatedViewController()
        super.init(viewController: moduleViewController)

        let interactor = TopRatedInteractor()
        let presenter = TopRatedPresenter(view: moduleViewController, interactor: interactor, wireframe: self)
        moduleViewController.presenter = presenter
    }

}

// MARK: - Extensions -

extension TopRatedWireframe: TopRatedWireframeInterface {

    func goToShowDetails(with id: String) {
        navigationController?.pushWireframe(ShowDetailsWireframe(with: id))
    }

    func goToSettings() {
        navigationController?.present(
            UINavigationController(rootViewController: SettingsWireframe().viewController),
            animated: true,
            completion: nil
        )
    }
}
